import React from 'react';
// import { makeStyles } from '@material-ui/core/styles';

export default function ProductCard({product, profileID}) {
  // console.log(product)
  async function handleClick(item){
    console.log(item)
    fetch("http://localhost:3000/cart_items",{
        method: "POST",
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
            profile_id: profileID,
            product_id: item.id
        })
    }).then(response => response.json())
    .then(data => console.log(data))
  }
  return (
    <div className="row" style={{width: "18rem"}}>
    <img src={product.image} class="card-img-top" alt={product.name}/>
    <div className="col prod-container">
      <h3 className="prod-name">{product.name}</h3>
      <p className="prod-name">${product.price}.99</p>
      <p className="prod-name">{product.quantity} left in stock.</p>    
      <a href="/cart" onClick={handleClick} className="prod-button btn btn-primary">Add to Cart</a>
      <br/>
      <br/>
    </div>
  </div>

  );
}
