import React from 'react'
import {makeStyles} from '@material-ui/core/styles';
import { useEffect, useState } from 'react'

function Cart({products, profileID}) {

    const [cartItem, setCartItem] = useState([])
    // const [filter, setFilter] = useState([])

    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
        },
        title: {
            color: '#fff'
        }
    }));
    const classes = useStyles()
    const API = `http://localhost:3000/profiles/${profileID}`
    useEffect(() => {
        async function fetchProducts(){
          const result = await fetch(API);
          const items = await result.json();
          setCartItem(items.products)
        }
        fetchProducts()

      }, [])
    let product = []
     product = cartItem.map(item => {
        return <div className={classes.root}>
                <br/><br/><br/><br/><br/>
                <h1 className={classes.title}>Cart</h1>
                <div className="card" style={{ width: '18rem' }}>
                <img variant="top" src={item.image} alt={item.name}/>
                <div>
                    <h3>{item.name}</h3>
                    <small>${item.price}.00</small>
                    <br/>
                    <button variant="primary">Remove from Cart</button>
                </div>
                <br/><br/><br/><br/><br/><br/><br/><br/>
                </div>
             </div>
    })

    return (
        {product}
    )
}

export default Cart
