import React from 'react'
import {makeStyles} from '@material-ui/core/styles';
import { useEffect, useState } from 'react'

function Cart({ profileID }) {
    const [cartItem, setCartItem] = useState([])
    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
        },
    }));
    const classes = useStyles()
    const API = "http://localhost:3000/cart_items"
    useEffect(() => {
        async function fetchProducts(){
          const result = await fetch(API);
          const items = await result.json();
          setCartItem(items)
        }
        fetchProducts()

      }, [])
      const userCart = cartItem.filter(item => item.profile_id === profileID)

    return (
        <div>
            <h1 className={classes.title}>Cart</h1>
            <div class="card" style={{"width": "18rem;"}}>
            <img src="..." class="card-img-top" alt="..."/>
            <div class="card-body">
                <h5 class="card-title">Card title</h5>
                <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                <a href="/" class="btn btn-primary">Go somewhere</a>
            </div>
            </div>
        </div>
    )
}

export default Cart
