import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar, IconButton, Toolbar } from '@material-ui/core';
import SortIcon from '@material-ui/icons/Sort';
import { useHistory } from 'react-router-dom';
import Navbar from './Navbar'

function Header() {
    let history = useHistory();

    const useStyles = makeStyles((theme) => ({

        root: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            color: '#fff',
            fontFamily: 'Nunito',
            // textAlign: 'center',
        },
        button: {
            color:'#fff',
            backgroundColor: '#FCC4D6',
            fontSize: '1.5rem',
            margin: '1.5px',
            borderRadius: 3,
            borderColor: 'rgba(0,0,0,0.08)',
            borderWidth: 0.3,
        },
        appbarWrapper:{
            width: '80%',
            margin: 'auto',
        },
        appbarTitle: {
            flexGrow: '2',
        },
        colorText: {
            color:'#FCC4D6',
        },
        title: {
            fontSize: '3rem'
        },
        down: {
            color:'#FCC4D6',
            fontSize: '2rem',
        },
    }));
    const classes = useStyles()

    function handleClick() {
        history.push('/shop')
    }

    return (
        <div className={classes.root}>
            <Navbar />
            <div className={classes.container}>
            <h1 className={classes.title}>Welcome to <br/> Jay's<span className={classes.colorText}>List</span></h1>
            <br/>
            <br/>
            <br/>
            <button onClick={handleClick} type="button" className={classes.button}>View Products</button>
            <button type="button" class={classes.button}>Log in</button>
            </div>
        </div>
    );
}

export default Header;
