import React from 'react'
import {makeStyles} from '@material-ui/core/styles';
import { useEffect, useState } from 'react'

function Cart({ profileID, products, setProductID, productID }) {
    const [cartItem, setCartItem] = useState([])
    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
        },
    }));
    const classes = useStyles()
    const API = "http://localhost:3000/cart_items"
    useEffect(() => {
        async function fetchProducts(){
          const result = await fetch(API);
          const items = await result.json();
          setCartItem(items)
        }
        fetchProducts()

      }, [])
      const userCart = cartItem.filter(item => item.profile_id === profileID)
      userCart.map(item => setProductID(item.product_id))
      const item = products.filter(item => item.id === productID)
      console.log(item)
      const cartCard = item.map(item => {
          return <div>
            <div className="card" style={{"width": "18rem;"}}>
            <img src={item.image} className="card-img-top" alt={item.name}/>
            {/* <br/> */}
            <div class="card">
                <h5 className="card-title">{item.name}</h5>
                <p className="card-text">${item.price}.00</p>
                <a href="/" className="btn btn-primary">Go somewhere</a>
            </div>
            </div>
          </div>
      })
    return (
        <div>
            <br/><br/><br/><br/><br/>
            <h1 className={classes.title}>Cart</h1>
            {cartCard}
        </div>
    )
}

export default Cart
