import React from 'react'
import {makeStyles} from '@material-ui/core/styles';
import { useEffect, useState } from 'react'
import { Card, Button } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';

function Cart({ profileID, products, setProductID, productID }) {
    const [cartItem, setCartItem] = useState([])
    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
        },
    }));
    const classes = useStyles()
    const API = "http://localhost:3000/cart_items"
    useEffect(() => {
        async function fetchProducts(){
          const result = await fetch(API);
          const items = await result.json();
          setCartItem(items)
        }
        fetchProducts()

      }, [])
      const userCart = cartItem.filter(item => item.profile_id === profileID)
      userCart.map(item => setProductID(item.product_id))
      const item = products.filter(item => item.id === productID)
      console.log(item)
      const cartCard = item.map(item => {
          return <Card style={{ width: '18rem' }}>
          <Card.Img variant="top" src={item.image} />
          <Card.Body>
            <Card.Title>{item.name}</Card.Title>
            <Card.Text>
              ${item.price}.00
            </Card.Text>
            <Button onClick={handleClick(item)} variant="primary">Add to Cart</Button>
          </Card.Body>
        </Card>
      })

      async function handleClick(){
        History.push('/cart')
        const res = await fetch("http://localhost:3000/cart_items/",{
            method: "POST",
            headers: {"Content-Type" : "application/json"},
            body: JSON.stringify({
                profile_id: item.profile_id,
                product_id: item.product_id
            })
        })
        const items = await res.json();
        if(res.ok){
        console.log("CartItem created: ", item)
      }
      else{
          console.log("Error:", item.Errors)
      }
    }
    return (
        <div>
            <br/><br/><br/><br/><br/>
            <h1 className={classes.title}>Cart</h1>
            {cartCard}
        </div>
    )
}

export default Cart
