import React from 'react'
import {makeStyles} from '@material-ui/core/styles';
import { useEffect, useState } from 'react'

function Cart({products, findProduct, product, profileID}) {

    const [cartItem, setCartItem] = useState([])
    // const [filter, setFilter] = useState([])

    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
        },
        title: {
            color: '#fff'
        }
    }));
    const classes = useStyles()
    const API = 'http://localhost:3000/cart_items'
    useEffect(() => {
        async function fetchProducts(){
          const result = await fetch(API);
          const items = await result.json();
          setCartItem(items)
        }
        fetchProducts()

      }, [])
      const userCart = cartItem.filter(item => item.profile_id === profileID)
      console.log(userCart)
    //   userCart.map(item => findProduct(item.product_id))
    return (
        <div className={classes.root}>
            <br/><br/><br/><br/><br/>
            <h1 className={classes.title}>Cart</h1>
            <div className="card" style={{ width: '18rem' }}>
            <img variant="top" src={cartItem.image} alt={cartItem.name}/>
            <div>
                <h3>{cartItem.name}</h3>
                <small>${cartItem.price}.00</small>
                <br/>
                <button variant="primary">Remove from Cart</button>
            </div>
            <br/><br/><br/><br/><br/><br/><br/><br/>
            </div>
        </div>
    )
}

export default Cart
