import React from 'react'
import {makeStyles} from '@material-ui/core/styles';
import { useState } from 'react';

function LogIn() {

    const [password, setPassword] = useState("")
    const [email, setEmail] = useState("")

    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
            paddingTop: '10rem',
        },
        colorText: {
            color:'#FCC4D6',
        },
        title: {
            fontSize: '3rem',
            color: '#fff'
        },
    }));
    const classes = useStyles()
    async function handleLogIn(e) {
        e.preventDefault();
        const newLogIn = {email: email, password: password}
        const response = await fetch("http://localhost:3000/login",{
            method: "POST",
            headers: { "Content-Type" : "application/json" },
            body: JSON.stringify(newLogIn)
        });
        const data = await response.json()
        console.log(response)
        // if (response.ok){
        //     console.log("message:", data)
        // }
        // else{
        //     console.log("errors: ", data.errors)
        // }
    }

    return (
        <div className={classes.root}>
            <h1 className={classes.title}>Log<span className={classes.colorText}>In</span></h1>
            <form className="login-form">  
            <div className="form-group">
                <label for="exampleInputEmail1">Email address</label>
                <input  onChange={(e) => setEmail(e.target.value)} type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/><br/>
                <small id="emailHelp" className="form-text text-muted bg-light">We'll never share your email with anyone else.</small>
            </div>
            <div className="form-group">
                <label for="exampleInputPassword1">Password</label>
                <input  onChange={(e) => setPassword(e.target.value)} type="password" className="form-control" id="exampleInputPassword1"/>
            </div>
            <div className="form-group form-check">
                <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
                <label className="form-check-label" for="exampleCheck1">Check me out</label>
            </div>
            <button onSubmit={handleLogIn} type="submit" className="btn btn-primary">Submit</button>
        </form>
        </div>
    )
}

export default LogIn
