import React from 'react'
import {makeStyles} from '@material-ui/core/styles';
import { useEffect, useState } from 'react'
import { Card, Button } from 'react-bootstrap';
import { useHistory } from 'react-router-dom';

function Cart({ profileID, products, setProductID, productID, setProfileID, findProduct, product}) {
    const [cartItem, setCartItem] = useState([])
    const [filter, setFilter] = useState([])
    const [isID, setID] = useState([])

    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("htttps://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
        },
    }));
    const classes = useStyles()
    const API = "http://localhost:3000/cart_items"
    useEffect(() => {
        async function fetchProducts(){
          const result = await fetch(API);
          const items = await result.json();
          setCartItem([...items])
        }
        fetchProducts()

      }, [])
      const userCart = cartItem.filter(item => item.profile_id === profileID)
      userCart.map(item => findProduct(item.product_id))
    //   console.log(product)
    return (
        <div>
            <br/><br/><br/><br/><br/>
            <h1 className={classes.title}>Cart</h1>
            <Card style={{ width: '18rem' }}>
            <Card.Img variant="top" src="holder.js/100px180" />
            <Card.Body>
                <Card.Title>{product[0].name}</Card.Title>
                <Card.Text>${product[0].price}.oo
                </Card.Text>
                <Button variant="primary">Remove from Cart</Button>
            </Card.Body>
            </Card>
        </div>
    )
}

export default Cart
