import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import ProductCard from './ProductCard'

function ProductContainer({products, profileID}) {
    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            // minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
        },
        colorText: {
            color:'#FCC4D6',
            paddingTop: '3rem'
        },
        title: {
            fontSize: '3rem',
            color: '#fff'
        },
    }));
    const classes = useStyles()
    const product = products.map(item => <ProductCard product={item} profileID={profileID}/> )

    return (
        <div className="prod-container mb-4 row">
        <h1 className={classes.title}>All <span className={classes.colorText}>Products</span></h1>
        {product}
        <br/>
        </div>
    )
}

export default ProductContainer
