import React from 'react'
import {makeStyles} from '@material-ui/core/styles';

function CartItem({item}) {
    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
        },
        title: {
            color: '#fff'
        }
    }));
    const classes = useStyles()
    return (
        <div className={classes.root}>
        <br/><br/><br/><br/><br/>
        <h1 className={classes.title}>Cart</h1>
        <div className="card" style={{ width: '18rem' }}>
        <img variant="top" src={item.image} alt={item.name}/>
        <div>
            <h3>{item.name}</h3>
            <small>${item.price}.00</small>
            <br/>
            <button variant="primary">Remove from Cart</button>
        </div>
        <br/><br/><br/><br/><br/><br/><br/><br/>
        </div>
     </div>
    )
}

export default CartItem
