import './App.css';
import Home from './Components/Home';
import { useEffect, useState} from 'react';
import {BrowserRouter as Router, Switch,Route} from "react-router-dom"
import ProductContainer from './Components/ProductContainer'
import Footer from './Components/Footer'
import Navbar from './Components/Navbar'
// import Profile from './Components/Profile'
import Cart from './Components/Cart'
import LogIn from './Components/LogIn'


function App() {
  const [products, setProducts] = useState([])
  const [profileID, setProfileID] = useState(1)
  const [productID, setProductID] = useState([])
  const [isLoggedIn, setisLoggedIn] = useState(false)
  const [item, setItem] = useState([])
  // const [cart, setCart] = useState([])

  function findProduct({prodID}) {
    let product = products.filter(item => item.id === prodID)
    console.log(prodID)
    setItem(product)
  }
  const API = "http://localhost:3000/products"
  useEffect(() => {
    async function fetchProducts(){
      const result = await fetch(API);
      const items = await result.json();
      setProducts(items)
    }
    fetchProducts()

  }, [])


  return (
    <div className="App">
      <Navbar/>
      <Router>
          <Switch>
          <Route exact path="/" component={() => <Home products={products}/>} />
          <Route exact path="/shop" component={() => <ProductContainer products={products} setProductID={setProductID} productID={productID} setprofileID={setProfileID} profileID={profileID}/>} />
          <Route exact path="/cart" component={() => <Cart products={products} product={item} profileID={profileID} findProduct={findProduct}/>} />
          <Route exact path="/log-in" component={() => <LogIn isLoggedIn={isLoggedIn} setisLoggedIn={setisLoggedIn} setProfileID={setProfileID} profileID={profileID}/>} />
         </Switch>
      </Router>
      <Footer/>
    </div>
  );
}

export default App;
