import React from 'react';
// import { makeStyles } from '@material-ui/core/styles';

export default function ProductCard({product, profileID}) {
  // console.log(product)
  async function handleClick(item){
    // history.push('/cart')
    const res = await fetch("http://localhost:3000/cart_items",{
        method: "POST",
        headers: {"Content-Type" : "application/json"},
        body: JSON.stringify({
            profile_id: profileID,
            product_id: item.id
        })
    })
    const items = await res.json();
    if(res.ok){
    console.log("CartItem created: ", items)
  }
  else{
      console.log("Error:", items.Errors)
  }
}
  return (
    <div class="card row" style={{width: "18rem"}}>
  <img src={product.image} class="card-img-top" alt="..."/>
  <div className="card">
    <h5 className="card-title">{product.name}</h5>
    <p className="card-text">${product.price}.99</p>
    <p className="card-text">{product.quantity} left in stock.</p>    
    <a href="/cart" onClick={handleClick} class="btn btn-primary">Add to Cart</a>
  </div>
</div>

  );
}
