import React from 'react'
import {makeStyles} from '@material-ui/core/styles';
import { useState } from 'react';
import { Form, Button } from 'react-bootstrap';


function LogIn() {

    const [password, setPassword] = useState("")
    const [email, setEmail] = useState("")

    const useStyles = makeStyles((theme) => ({
        root: {
            backgroundImage: `url("https://images.pexels.com/photos/1229861/pexels-photo-1229861.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500")`,
            minHeight: '100vh',
            backgroundRepeat: 'no-repeat',
            backgroundSize: 'cover',
            paddingTop: '10rem',
            color: 'white',
        },
        colorText: {
            color:'#FCC4D6',
        },
        title: {
            fontSize: '3rem',
            color: '#fff'
        },
    }));
    const classes = useStyles()
    async function handleLogIn(e) {
        e.preventDefault();

        const newLogIn = {email: email, password: password}
        const reqObj = {}
        reqObj.headers = {"Content-Type": "application/json"}
        reqObj.method = "POST"
        reqObj.body = JSON.stringify(newLogIn)

        const res = await fetch("http://localhost:3000/login", reqObj)
        const data = await res.json()
        console.log(data.body)
    }

    return (
        <div className={classes.root}>
            <h1 className={classes.title}>Log<span className={classes.colorText}>In</span></h1>
            <Form>
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control onChange={(e) => setEmail(e.target.value)} type="email" placeholder="Enter email" />
                <br/>
                <Form.Text className="text-muted">
                We'll never share your email with anyone else.
                </Form.Text>
            </Form.Group>

            <Form.Group className="mb-3" size="lg" controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control  onChange={(e) => setPassword(e.target.value)} type="password" placeholder="Password" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>
            <Button onSubmit={handleLogIn} variant="primary" type="submit">
                Submit
            </Button>
            </Form>
        </div>
    )
}

export default LogIn
