import React from 'react'
import Button from '@material-ui/core/Button';
import ProductCard from './ProductCard'
import { useEffect, useState} from 'react';
import NavBar from './NavBar'
import Cart from './Cart'

function ProductContainer({products, cart, setCart}) {
    // const [product, setProduct] = useState([])
    const elem = products.map(product => {
        return( <div className="cardContainer mb-3" style={{"maxWidth": "540px"}}>
            <div className="col no-gutters">
                <div className="col md-4 ">
                <img src={product.image} alt={product.name} className="productImage"/>
                {/* </div>
                <div className="col-md-8 col"> */}
                <div className="card-body ">
                    <h5 className="card-title">{product.name}</h5>
                    <p className="card-text">${product.price}.99</p>
                    <Button variant="contained" color="primary" 
                    onClick={() => addtoCart(product)}>
                    Add to Cart
                    </Button>
                </div>
                </div>
            </div>
            </div>)})

    function addtoCart(product){
        console.log(product)
        const newCart = {product_id: product.id, user_id: 1}
        // fetch("http://localhost:3000/cart",{
        //     method: "POST", 
        //     headers: {
        //         // "Accept": "application/json", 
        //         "Content-Type": "application/json"
        //       }, 
        //     body: JSON.stringify({product_id: 1, user_id: 1}),

        // }).then(response=>response.json())
        // .then(data=>console.log([...cart,data]))

        fetch('http://localhost:3000/cart/new', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(newCart)
        })
        .then(response => response.json())
        .then(data => {
        console.log('Success:', data);
        })
    }
    return (
        <div className="row">
            <NavBar/>
            <br/>
          {elem}
        </div>
    )
}

export default ProductContainer
