import React from 'react'
import { Form, Row, Col } from 'react-bootstrap'
import { makeStyles } from '@material-ui/core/styles';
import { useState } from 'react'

function EditProfile({setName, name}) {

    const [email, setEmail] = useState("")
    const [bio, setBio] = useState("")
    const [password, setPassword] = useState("")
    const [location, setLocation] = useState("")
    const [gender, setGender] = useState("")

    const useStyles = makeStyles((theme) => ({
        root: {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            color: '#fff',
            fontFamily: 'Nunito',
            // textAlign: 'center',
        },
        button: {
            color:'#fff',
            backgroundColor: '#FCC4D6',
            fontSize: '1.5rem',
            margin: '1.5px',
            borderRadius: 3,
            borderColor: 'rgba(0,0,0,0.08)',
            borderWidth: 0.3,
        },
        colorText: {
            color:'#FCC4D6',
        },
        title: {
            fontSize: '3rem'
        },
    }));
    const classes = useStyles()

    function handleClick() {

    }
    return (
        <div className="editProfile">
            <h1 className={classes.title}>Update <span className={classes.colorText}>Profile</span></h1>

            <br/><br/>
            <Form>
            <Row>
                <Col>
                Username:
                <Form.Control onChange={(e) => console.log(e.target.value)} placeholder="Username" />
                </Col>

            </Row>
            <br/>
            <Form.Group className="mb-3" controlId="formGroupEmail">
                <Form.Label>Email address:</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
            </Form.Group>
            <Form.Group className="mb-3" controlId="formGroupPassword">
                <Form.Label>Password:</Form.Label>
                <Form.Control type="password" placeholder="Enter Password" />
            </Form.Group>
            <Row>
                <Col>
                Location:
                <Form.Control placeholder="Location" />
                </Col>
                <Col>
                Gender:
                <Form.Control placeholder="Gender" />
                </Col>
            </Row>
            <br/>
            <Form.Group className="mb-3" controlId="formGroupPassword">
                <Form.Label>Bio:</Form.Label>
                <Form.Control type="Bio" placeholder="Bio" />
            </Form.Group>
            <button onClick={handleClick}className="profile-btn">Save Changes</button>

            </Form>
        </div>
    )
}

export default EditProfile
